const css = // css
`
    @keyframes strokeOffsetHoverAnimation {
        0% {
            stroke-dashoffset: 0;
        }

        32% {
            stroke-dashoffset: 182.28;
        }

        100% {
            stroke-dashoffset: 364.56;
        }
    }
    
    @keyframes strokeOffsetLoadingAnimation {
        0% {
            stroke-dashoffset: 182.28;
        }

        60% {
            stroke-dashoffset: 364.56;
        }

        83.4% {
            left: 50%;
            transform: translateX(-50%);
            stroke-dashoffset: 364.56;
            width: 160px;
            height: 160px;
        }

        100% {
            left: 0;
            transform: translateX(0px);
            stroke-dashoffset: 364.56;
            width: 60px;
            height: 60px;
        }
    }

    @keyframes positionAnimation {
        0% {
            width: 100vw;
            height: 100vh;
        };

        50% {
            width: 160px;
            height: 160px;
        }

        100% {
            top: 50px;
            width: 60px;
            height: 60px;
        }
    }

    #logo {
        display: flex;
        align-items: center;
        width: min(100vw, 1120px);
        height: 100vh;
        position: fixed;
        top: 0;
        left: 0;
        right: 0;
        margin: 0 auto;
        box-sizing: border-box;
        animation: positionAnimation .2s ease-out forwards;
        animation-delay: 1s;
        z-index: 100;
    }

    #logo > svg {
        width: 60px;
        height: 60px;
        stroke-dasharray: 182.28;
    }

    #logo.loading > svg {
        position: absolute;
        left: 50%;
        transform: translateX(-50%);
        width: 160px;
        height: 160px;
        stroke-dasharray: 182.28;
        animation: strokeOffsetLoadingAnimation 1.2s linear forwards;
    }

    #logo.animate-on-hover:hover > svg {
        animation: strokeOffsetHoverAnimation 2s cubic-bezier(.58,.58,0,1);
    }
`;

const Logo = createComponent(html, css);

document.addEventListener('DOMContentLoaded', () => {
    setTimeout(() => {
        Logo.classList.remove('loading');
        Logo.classList.add('animate-on-hover');
    }, 1200);
});

export default Logo;
